import "core-js/modules/es6.function.name";
import "core-js/modules/es6.regexp.to-string";
import "core-js/modules/es6.number.constructor";
export default {
  name: 'Manage',
  data: function data() {
    return {
      tableHeight: '',
      manageData: [],
      dialogAdd: false,
      addForm: {
        img: '',
        imageUrl: '',
        role: '',
        name: '',
        pwd: ''
      },
      addRules: {
        role: [{
          required: true,
          message: '请至少选择一个活动性质',
          trigger: 'change'
        }],
        name: [{
          required: true,
          message: '请输入用户名',
          trigger: 'blur'
        }, {
          min: 5,
          max: 10,
          message: '长度在 5 到 10 个字符',
          trigger: 'blur'
        }],
        pwd: [{
          required: true,
          message: '请输入密码',
          trigger: 'blur'
        }, {
          min: 6,
          max: 12,
          message: '长度在 6 到 12 个字符',
          trigger: 'blur'
        }]
      },
      dialogEdit: false,
      editForm: {
        index: Number,
        unionid: '',
        img: '',
        imageUrl: '',
        role: '',
        name: '',
        pwd: '******'
      },
      editRules: {
        role: [{
          required: true,
          message: '请至少选择一个活动性质',
          trigger: 'change'
        }],
        name: [{
          required: true,
          message: '请输入用户名',
          trigger: 'blur'
        }, {
          min: 5,
          max: 10,
          message: '长度在 5 到 10 个字符',
          trigger: 'blur'
        }],
        pwd: [{
          required: true,
          message: '请输入密码',
          trigger: 'blur'
        }, {
          min: 6,
          max: 12,
          message: '长度在 6 到 12 个字符',
          trigger: 'blur'
        }]
      },
      dialogDel: false,
      delForm: {
        index: Number,
        unionid: ''
      }
    };
  },
  created: function created() {
    var that = this;
    that.$axios.post('/api/manage/list', {}, {}).then(function (res) {
      that.manageData = res.data;
    }).catch(function (err) {
      that.$toast(that, 'error', '网络错误～');
    });
  },
  mounted: function mounted() {
    var that = this;
    that.tableHeight = that.$refs.manage.offsetHeight;
  },
  methods: {
    onFilter: function onFilter(role) {
      switch (role) {
        case 0:
          return '超级管理员';

        case 1:
          return '管理员';

        case 2:
          return '文案编辑';
      }
    },
    handleAdd: function handleAdd(index, row) {
      this.dialogAdd = true;
    },
    beforeAvatarUpload: function beforeAvatarUpload(file) {
      var that = this;
      var isJPG = file.type === 'image/jpeg' || file.type === 'image/png';
      var isLt1M = file.size / 1024 / 1024 < 1;

      if (!isJPG) {
        that.$toast(that, 'error', '上传头像图片只能是 JPG PNG 格式!');
        return;
      }

      if (!isLt1M) {
        that.$toast(that, 'error', '上传头像图片大小不能超过 1MB!');
        return;
      }

      return isJPG && isLt1M;
    },
    handleAvatarSuccess: function handleAvatarSuccess(res, file) {
      if (this.dialogAdd) {
        this.addForm.img = res;
        this.addForm.imageUrl = URL.createObjectURL(file.raw);
      } else if (this.dialogEdit) {
        this.editForm.img = res;
        this.editForm.imageUrl = URL.createObjectURL(file.raw);
      }
    },
    onAddSubmit: function onAddSubmit(addForm) {
      var that = this;
      that.$refs[addForm].validate(function (valid) {
        if (valid) {
          that.$axios.post('/api/manage/add', that.$qs.stringify(that.addForm), {}).then(function (res) {
            if (res.data.status) {
              that.dialogAdd = false;
              that.manageData.push(res.data.data[0]);
              that.$toast(that, 'success', res.data.message);
            } else {
              that.$toast(that, 'error', res.data.message);
            }
          }).catch(function (err) {
            that.$toast(that, 'error', '网络错误～');
          });
        } else {
          return false;
        }
      });
    },
    handleEdit: function handleEdit(index, row) {
      this.editForm.index = index;
      this.editForm.unionid = this.manageData[index].unionid;
      this.editForm.img = this.manageData[index].img;
      this.editForm.imageUrl = this.manageData[index].icon;
      this.editForm.role = this.manageData[index].role.toString();
      this.editForm.name = this.manageData[index].name;
      this.dialogEdit = true;
    },
    onEditSubmit: function onEditSubmit(editForm) {
      var that = this;
      that.$refs[editForm].validate(function (valid) {
        if (valid) {
          that.$axios.post('/api/manage/edit', that.$qs.stringify(that.editForm), {}).then(function (res) {
            if (res.data.status) {
              that.manageData[that.editForm.index].role = res.data.data[0].role;
              that.manageData[that.editForm.index].name = res.data.data[0].name;
              that.manageData[that.editForm.index].time = res.data.data[0].time;
              that.manageData[that.editForm.index].img = res.data.data[0].img;
              that.manageData[that.editForm.index].icon = res.data.data[0].icon;
              that.dialogEdit = false;
              that.$toast(that, 'success', res.data.message);
            } else {
              that.$toast(that, 'error', res.data.message);
            }
          }).catch(function (err) {
            that.$toast(that, 'error', '网络错误～');
          });
        } else {
          return false;
        }
      });
    },
    handleDelete: function handleDelete(index, row) {
      this.delForm.index = index;
      this.delForm.unionid = row.unionid;
      this.dialogDel = true;
    },
    onDelSubmit: function onDelSubmit() {
      var that = this;
      var data = {
        'unionid': that.delForm.unionid
      };
      that.$axios.post('/api/manage/del', that.$qs.stringify(data), {}).then(function (res) {
        if (res.data.status) {
          that.manageData.splice(that.delForm.index, 1);
          that.dialogDel = false;
          that.$toast(that, 'success', res.data.message);
        } else {
          that.$toast(that, 'error', res.data.message);
        }
      }).catch(function (err) {
        that.$toast(that, 'error', '网络错误～');
      });
    }
  }
};
